import React from 'react';
import './settings.css';
import background1 from '../images/background1.jpeg';
import background2 from '../images/background2.jpeg';
import background3 from '../images/background3.jpeg';

const Settings = ({ selectedBackground, handleBackgroundChange }) => {
  const handleBackgroundClick = (background) => {
    handleBackgroundChange(background);
  };

  return (
    <div className="settings-container">
      <div className="settings-content">
        <h2>Settings</h2>
        <label htmlFor="backgroundSelect">Select Background:</label>
        <div className="settings-navigation">
          <button onClick={() => handleBackgroundClick(background1)}><img src={background1} alt="Background 1" /></button>
          <button onClick={() => handleBackgroundClick(background2)}><img src={background2} alt="Background 2" /></button>
          <button onClick={() => handleBackgroundClick(background3)}><img src={background3} alt="Background 3" /></button>
        </div>
      </div>
    </div>
  );
};

export default Settings;
