import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from '../src/pages/home';
import Ambient from '../src/pages/ambient';
import Focus from '../src/pages/focus';
import Settings from './components/Settings';
import Navbar from './components/Navbar';

function App() {
  const [selectedBackground, setSelectedBackground] = useState('default');

  const handleBackgroundChange = (background) => {
    setSelectedBackground(background);
  };

  return (
    <Router>
      <Navbar />
      <Routes>
        <Route path="/" element={<Home selectedBackground={selectedBackground} />} />
        <Route path="/ambient" element={<Ambient selectedBackground={selectedBackground} />} />
        <Route path="/focus" element={<Focus selectedBackground={selectedBackground} />} />
        <Route
          path="/settings"
          element={<Settings selectedBackground={selectedBackground} handleBackgroundChange={handleBackgroundChange} />}
        />
      </Routes>
    </Router>
  );
}

export default App;
