import React from 'react';
import './App.css';
import { BackgroundProvider, useBackground } from './BackgroundContext';
import Button from './components/Button';
import Home from './components/Home'; // Assuming you have a Home component

function App() {
  const { backgroundColor } = useBackground();

  return (
    <div className="App" style={{ backgroundColor }}>
      <header className="App-header">
        <h1 className="home-title">Orphedy</h1>
        <p className="home-caption">study with the one and only: Orpheus</p>
        <Button variant="primary" to="/home">let's study</Button>
      </header>
    </div>
  );
}

const AppWithBackground = () => {
  return (
    <BackgroundProvider>
      <App />
    </BackgroundProvider>
  );
};

export default AppWithBackground;
