import React, { useState } from 'react';
import './settings.css';
import background1 from '../images/background1.jpeg';
import background2 from '../images/background2.jpeg';
import background3 from '../images/background3.jpeg';
import BackgroundButton from './BackgroundButton';
import { useHistory } from 'react-router-dom';

const Settings = ({ showSettings, onClose }) => {
  const [selectedBackground, setSelectedBackground] = useState('default');

  const handleBackgroundChange = (e) => {
    setSelectedBackground(e.target.value);
    // You can perform additional actions here based on the selected background option
  };

  const history = useHistory();

  const handleOnClick = () => {
    const newData = 'example data'; // Set your data here
    setSelectedBackground(newData);
    history.push('/result'); // Navigate to the ResultPage
  };

  return (
    <div className={showSettings ? 'settings-container show' : 'settings-container'}>
      <div className="settings-content">
        <h2>Settings</h2>
        <label htmlFor="backgroundSelect" value={selectedBackground} onChange={handleBackgroundChange}>Select Background:</label>
        <div className="settings-navigation">
            <BackgroundButton onClick={() => console.log("background 1")} backgroundSrc={background1} altText="Background 1" />
            {/* <button onClick={() => handleBackgroundChange('background1')}><img src={background1} alt="Background 1" /></button> */}
            <button onClick={() => handleBackgroundChange('background2')}><img src={background2} alt="Background 2" /></button>
            <button onClick={() => handleBackgroundChange('background3')}><img src={background3} alt="Background 3" /></button>
        </div>
      </div>
      <button className="close-button" onClick={onClose}>X</button>
    </div>
  );
};

export default Settings;
