import React, { useState, useEffect } from 'react';
import Button from '../components/Button';
import 'study-with-orpheus/src/css/home.css';

function Home() {
  const [currentTime, setCurrentTime] = useState(getCurrentTime());

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentTime(getCurrentTime());
    }, 1000);

    return () => clearInterval(intervalId);
  }, []);

  function getCurrentTime() {
    const now = new Date();
    const options = { hour: 'numeric', minute: 'numeric' };
    return now.toLocaleTimeString([], options);
  }

  return (
    <div style={{ position: 'relative', minHeight: '100vh' }}>
      <div style={{ textAlign: 'center', marginTop: '50vh', transform: 'translateY(-50%)' }}>
        <h2>see ya later cutie pie ;)</h2>
        <h1>{currentTime}</h1>
      </div>
      <div style={{ position: 'fixed', bottom: 0, right: 0, padding: '10px' }}>
        <Button style={{ marginRight: '10px' }}>Button 1</Button>
        <Button style={{ marginRight: '10px' }}>Button 2</Button>
        <Button style={{ marginRight: '10px' }}>Button 3</Button>
        <Button>Button 4</Button>
      </div>
    </div>
  );
}

export default Home;
