import React from 'react';
import styled from 'styled-components';

// Define styled component for the button
const StyledButton = styled.button`
  /* Add your CSS styles here */
  background-color: ${props => props.variant === 'primary' ? '#FBF7F2' : '#B69779'}; /* Use variant prop to determine background color */
  color: #fff; /* Text color */
  padding: ${props => props.size === 'small' ? '5px 10px' : '10px 20px'}; /* Use size prop to determine padding */
  font-size: 1em; /* Font size */
  border: none; /* Remove border */
  cursor: pointer; /* Add pointer cursor on hover */
`;

const Button = ({ type, variant, className, id, onClick, children, size }) => {
  return (
    <StyledButton
      type={type ? type : "button"}
      variant={variant}
      className={className}
      id={id}
      onClick={onClick}
      size={size}
    >
      {children}
    </StyledButton>
  );
};

export default Button;
