import React, { useState, useEffect } from 'react';
import './css/home.css';
import Navbar from '../components/Navbar';
import { selectedBackground } from '../components/Backgrounds';
import { settingsVariable } from '../components/Settings';

function Home() {
  const [currentTime, setCurrentTime] = useState(getCurrentTime());

  console.log('selectedBackground:', selectedBackground); // Add this line

  const pageStyles = {
    background: settingsVariable ? settingsVariable : 'defaultBackground', // Use the settingsVariable for background styling
    // Add other styles as needed
  };

  useEffect(() => {
    console.log('settingsVariable:', settingsVariable); // Add this line
    const intervalId = setInterval(() => {
      setCurrentTime(getCurrentTime());
    }, 1000);

    return () => clearInterval(intervalId);
  }, []);

  function getCurrentTime() {
    const now = new Date();
    const options = { hour: 'numeric', minute: 'numeric' };
    return now.toLocaleTimeString([], options);
  }

  console.log('pageStyles:', pageStyles); // Add this line

  return (
    <div className='study-container' style={pageStyles}>
      <div style={{ textAlign: 'center', marginTop: '50vh', transform: 'translateY(-50%)' }}>
        <h2>"insert random quote here"</h2>
        <h1 className='current-time'>{currentTime}</h1>
      </div>
      <Navbar />
    </div>
  );
}

export default Home;
