import React, { useContext, useState } from 'react';
import './settings.css';
import background1 from '../images/background1.jpeg';
import background2 from '../images/background2.jpeg';
import background3 from '../images/background3.jpeg';
import { BackgroundContext } from '../context/BackgroundContext';

export const Settings = ({ showSettings, onClose }) => {
  const { setHomeBackground, setFocusBackground } = useContext(BackgroundContext);
  const [localBackground, setLocalBackground] = useState('default');

  const handleBackgroundChange = (background, page) => {
    setLocalBackground(background); // Update local state in Settings
    if (page === 'home') {
      setHomeBackground(background); // Update home background state
    } else if (page === 'focus') {
      setFocusBackground(background); // Update focus background state
    }
  };

  return (
    <div style={{ 
      backgroundImage: `url(${localBackground})`,
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
    }} className={showSettings ? 'settings-container show' : 'settings-container'}>
      <div className="settings-content">
        <h2>Settings</h2>
        <label htmlFor="backgroundSelect">Select Background:</label>
        <div className="settings-navigation">
          <button onClick={() => handleBackgroundChange(background1, 'home')}><img src={background1} alt="Background 1" /></button>
          <button onClick={() => handleBackgroundChange(background2, 'home')}><img src={background2} alt="Background 2" /></button>
          <button onClick={() => handleBackgroundChange(background3, 'home')}><img src={background3} alt="Background 3" /></button>
        </div>
        <div className="settings-navigation">
          <button onClick={() => handleBackgroundChange(background1, 'focus')}><img src={background1} alt="Background 1" /></button>
          <button onClick={() => handleBackgroundChange(background2, 'focus')}><img src={background2} alt="Background 2" /></button>
          <button onClick={() => handleBackgroundChange(background3, 'focus')}><img src={background3} alt="Background 3" /></button>
        </div>
      </div>
      <span className="close-button" onClick={onClose}>X</span>
    </div>
  );
};

export default Settings;
