import React from 'react';
import styled from 'styled-components';
import { useHistory } from 'react-router-dom'; // Import useHistory hook

// Define styled component for the button
const StyledButton = styled.button`
  /* Button styles */
`;

const Button = ({ type, variant, className, id, onClick, children, size, to }) => {
  const history = useHistory(); // Initialize useHistory hook

  const handleClick = () => {
    if (to) {
      history.push(to); // Redirect to the specified URL
    }
    if (onClick) {
      onClick(); // Invoke the onClick function if provided
    }
  };

  return (
    <StyledButton
      type={type ? type : "button"}
      variant={variant}
      className={className}
      id={id}
      onClick={handleClick} // Call handleClick function on button click
      size={size}
    >
      {children}
    </StyledButton>
  );
};

export default Button;
