import './App.css';
import LearningPage from './LearningPage';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <h1 className="home-title">Orphedy</h1>
        <p className="home-caption">study with the one and only: Orpheus</p>
        <a href="/learning" className="action-button">Start Learning</a>
      </header>
      <Navbar />
        <div className="container">
          <Switch>
            <Route exact path="/" component={Home} />
            <Route path="/study-materials" component={StudyMaterials} />
            <Route path="/flashcards" component={Flashcards} />
            <Route path="/quiz" component={Quiz} />
            <Route path="/learning" component={LearningPage} /> {/* Route to the LearningPage */}
            <Route component={NotFound} />
          </Switch>
        </div>
    </div>
  );
}

export default App;
