import React, { useState } from 'react';
import './settings.css';
import background1 from '../images/background1.jpeg';
import background2 from '../images/background2.jpeg';
import background3 from '../images/background3.jpeg';
import BackgroundButton from './BackgroundButton';
import { selectedBackground } from './Backgrounds';

const Settings = ({ showSettings, onClose }) => {
  const [selectedColor, setSelectedColor] = useState(null);

  const handleColorChange = (color) => {
    setSelectedColor(color);
  };

  return (
    <div className={showSettings ? 'settings-container show' : 'settings-container'}>
      <div className="settings-content">
        <h2>Settings</h2>
        <div className="settings-navigation">
          <BackgroundButton onClick={() => handleColorChange('red')} backgroundSrc={background1} altText="Background 1" />
          <BackgroundButton onClick={() => handleColorChange('blue')} backgroundSrc={background2} altText="Background 2" />
          <BackgroundButton onClick={() => handleColorChange('green')} backgroundSrc={background3} altText="Background 3" />
        </div>
      </div>
      <button className="close-button" onClick={onClose}>X</button>
      {/* Use the selectedColor state variable to set the background */}
      <div className="content-container" style={{ backgroundColor: selectedColor }}>
        {/* Your content here */}
      </div>
    </div>
  );
};

export default Settings;
