import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import './settings.css';
import background1 from '../images/background1.jpeg';
import background2 from '../images/background2.jpeg';
import background3 from '../images/background3.jpeg';
import BackgroundButton from './BackgroundButton';

const Settings = ({ showSettings, onClose }) => {
  const navigate = useNavigate(); // Use useNavigate instead of useHistory
  const [selectedBackground, setSelectedBackground] = useState('default');

  const handleBackgroundChange = (background) => {
    setSelectedBackground(background);
    // Navigate to the same route with the selected background as a query parameter
    navigate(`/settings?background=${background}`);
  };

  return (
    <div className={showSettings ? 'settings-container show' : 'settings-container'}>
      <div className="settings-content">
        <h2>Settings</h2>
        <div className="settings-navigation">
          <BackgroundButton onClick={() => handleBackgroundChange('background1')} backgroundSrc={background1} altText="Background 1" />
          <BackgroundButton onClick={() => handleBackgroundChange('background2')} backgroundSrc={background2} altText="Background 2" />
          <BackgroundButton onClick={() => handleBackgroundChange('background3')} backgroundSrc={background3} altText="Background 3" />
        </div>
      </div>
      <button className="close-button" onClick={onClose}>X</button>
    </div>
  );
};

export default Settings;
