import React from 'react';
import './settings.css';
import background1 from '../images/background1.jpeg';
import background2 from '../images/background2.jpeg';
import background3 from '../images/background3.jpeg';
import BackgroundButton from './BackgroundButton';

const Settings = ({ showSettings, onClose }) => {

  return (
    <div className={showSettings ? 'settings-container show' : 'settings-container'}>
      <div className="settings-content">
        <h2>Settings</h2>
        <label htmlFor="backgroundSelect">Select Background:</label>
        <div className="settings-navigation">
          <BackgroundButton onClick={() => setSelectedBackground(background1)} backgroundSrc={background1} altText="Background 1" />
          <BackgroundButton onClick={() => setSelectedBackground(background2)} backgroundSrc={background2} altText="Background 2" />
          <BackgroundButton onClick={() => setSelectedBackground(background3)} backgroundSrc={background3} altText="Background 3" />
        </div>
      </div>
      <button className="close-button" onClick={onClose}>X</button>
    </div>
  );
};

export default Settings;
