import React, { useState, useEffect } from 'react';
import './css/home.css';
import Navbar from '../components/Navbar';
import { useLocation } from 'react-router-dom';
import Settings from '../components/Settings';

function Home() {
  const [currentTime, setCurrentTime] = useState(getCurrentTime());
  const location = useLocation();
  const data = location.state?.data || 'default data';

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentTime(getCurrentTime());
    }, 1000);

    return () => clearInterval(intervalId);
  }, []);

  const [selectedBackground, setSelectedBackground] = useState('default');

  function getCurrentTime() {
    const now = new Date();
    const options = { hour: 'numeric', minute: 'numeric' };
    return now.toLocaleTimeString([], options);
  }

  return (
    <div className='study-container' style={{ backgroundImage: {data}}}>
      <div style={{ textAlign: 'center', marginTop: '50vh', transform: 'translateY(-50%)' }}>
        <h2>see ya later cutie pie ;)</h2>
        <h1>{currentTime}</h1>
      </div>
      <Navbar />
    </div>
  );
}

export default Home;
