import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';

const StyledButton = styled.button`
  background-color: ${props => props.variant === 'primary' ? '#162A2C' : '#5E6C5B'}; /* Use variant prop to determine background color */
  color: #fff; 
  padding: ${props => props.size === 'small' ? '5px 10px' : '10px 20px'}; /* Use size prop to determine padding */
  font-size: 1em; 
  border: none; 
  cursor: pointer; 
  color: white;
  border-radius: 12px;
  padding: 15px;

  &:hover {
    background-color: ${props => props.variant === 'primary' ? '#5E6C5B' : '#162A2C'};
    border: 1px;
    border-color: pink;
    color: #fff;
  }
`;

const Button = ({ type, variant, className, id, onClick, children, size, to }) => {
  return (
    <Link to={to}>
        <StyledButton
        type={type ? type : "button"}
        variant={variant}
        className={className}
        id={id}
        onClick={onClick}
        size={size}
        >
        {children}
        </StyledButton>
    </Link>
  );
};

export default Button;
