import React, { useState } from 'react';
import './settings.css';
import background1 from '../images/background1.jpeg';
import background2 from '../images/background2.jpeg';
import background3 from '../images/background3.jpeg';
import BackgroundButton from './BackgroundButton';

const Settings = ({ showSettings, onClose }) => {
  const [selectedBackground, setSelectedBackground] = useState('default');

  const handleBackgroundChange = (e) => {
    setSelectedBackground(e.target.value);
  };

  return (
    <div style={{backgroundImage: '../images/background2.jpeg'}} className={showSettings ? 'settings-container show' : 'settings-container'}>
      <div className="settings-content">
        <h2>Settings</h2>
        <label htmlFor="backgroundSelect">Select Background:</label>
        <div className="settings-navigation">
          <button onClick={() => handleBackgroundChange('../images/background2.jpeg')}><img src={background2} alt="Background 1" /></button>
          <button onClick={console.log("CLICK BUTTON 2")}><img src={background2} alt="Background 2" /></button>
          <button onClick={console.log("U")}><img src={background3} alt="Background 3" /></button>
        </div>
      </div>
      <span className="close-button" onClick={onClose}>X</span>
    </div>
  );
};

export default Settings;
