import React, { useState, useEffect } from 'react';
import './css/home.css';
import Navbar from '../components/Navbar';
import { setSelectedBackground } from '../components/Backgrounds';

function Home() {
  const [currentTime, setCurrentTime] = useState(getCurrentTime());

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentTime(getCurrentTime());
    }, 1000);

    return () => clearInterval(intervalId);
  }, []);

  function getCurrentTime() {
    const now = new Date();
    const options = { hour: 'numeric', minute: 'numeric' };
    return now.toLocaleTimeString([], options);
  }

  const handleBackgroundChange = () => {
    setSelectedBackground('#0000ff');
  };

  return (
    <div className='study-container'>
      <div style={{ backgroundColor: `${setSelectedBackground}`, textAlign: 'center', marginTop: '50vh', transform: 'translateY(-50%)' }}>
        <h2>"insert random quote here"</h2>
        <h1 className='current-time'>{currentTime}</h1>
        {/* Button to change background color */}
        <button onClick={handleBackgroundChange}>Change Background</button>
      </div>
      <Navbar />
    </div>
  );
}

export default Home;
